import React, { useState } from "react";
import "./ExpenseForm";

const ExpenseForm = () => {
  const [enteredTitle, setEnteredTitle] = useState("");
  // enteredTitle is the title which was already there, setEnteredTitle is the new title
  const [enteredAmout, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  const titleChageHandler = (event) => {
    setEnteredTitle(event.target.value);
  };
  const amountChageHandler = (event) => {
    setEnteredAmount(event.target.value);
  };
  const dateChageHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__controls">
          <label>Title</label>
          <input type="text" onChange={titleChageHandler} />
        </div>
        <div className="new-expense__controls">
          <label>Amount</label>
          <input type="number" onChange={amountChageHandler} />
        </div>
        <div className="new-expense__controls">
          <label>Date</label>
          <input type="date" onChange={dateChageHandler} />
        </div>
      </div>
      <div className="new-expense__controls">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
